mapper.select:
select id, category, title, imgUrl, content, createdDate, modifiedDate
  from notice
 where id = #{id}

mapper.update:
update notice
   set category = #{noticeDto.category},title = #{noticeDto.title}, imgUrl = #{noticeDto.imgUrl}, content = #{noticeDto.content}, modifiedDate = now()
 where id = #{id}
